mapper.select:

			select rno, bno, replyer, content, replyDate, updateDate
		from (
  		 select /*+INDEX(comments id) */ 
     		rownum rno, bno, replyer, content, replyDate, updateDate
   from comments
   where rno =  #{rno}
   and id > 0
   and rownum <= #{cri.pageNum} * #{cri.amount} ) where rn > (#{cri.pageNum} -1) * #{cri.amount}
