mapper.select:

			select  id, rno, replyer, content, replyDate, updateDate
		from (
  		 select /*+INDEX(comments id) */ 
     		rownum rn, id, rno, replyer, content, replyDate, updateDate
   from comments
   where id =  #{id}
   and id > 0
   and rownum <= #{cri.pageNum} * #{cri.amount} ) where rn > (#{cri.pageNum} -1) * #{cri.amount}
